package nl.fontys.fhict.jea.gwt.jee7.server.test;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Calendar;
import java.util.Collections;
import java.util.GregorianCalendar;
import java.util.List;
import java.util.Random;
import java.util.logging.Level;
import java.util.logging.Logger;
import javax.annotation.PostConstruct;
import javax.annotation.Resource;
import javax.ejb.Asynchronous;
import javax.ejb.ConcurrencyManagement;
import javax.ejb.ConcurrencyManagementType;
import javax.ejb.SessionContext;
import javax.ejb.Singleton;
import javax.ejb.Startup;
import javax.ejb.TransactionAttribute;
import javax.ejb.TransactionAttributeType;
import javax.inject.Inject;
import javax.persistence.EntityManager;
import javax.persistence.PersistenceContext;
import nl.fontys.fhict.jea.gwt.jee7.server.bus.Broadcaster;
import nl.fontys.fhict.jea.gwt.jee7.server.util.Encryption;
import nl.fontys.fhict.jea.gwt.jee7.shared.bus.ClientIncoming;
import nl.fontys.fhict.jea.gwt.jee7.shared.bus.test.CurrentTime;
import nl.fontys.fhict.jea.gwt.jee7.shared.domain.Role;
import nl.fontys.fhict.jea.gwt.jee7.shared.domain.Tweet;
import nl.fontys.fhict.jea.gwt.jee7.shared.domain.User;

/**
 *
 * @author jgeenen
 */
@Singleton
@Startup
@ConcurrencyManagement(ConcurrencyManagementType.BEAN)
public class PushyBean {

    public static final String TOPIC = "public";

    @Inject
    Broadcaster broadcaster;

    @Resource
    SessionContext ctx;

    @PersistenceContext
    EntityManager em;

    private void pushAsync() throws IllegalStateException {
        ctx.getBusinessObject(PushyBean.class).push();
    }

    private void fillDB() {
        final Role policeman = new Role(Role.POLICEMAN, "a policeman");
        final Role civilServant = new Role(Role.CIVIL_SERVANT, "a civil cervant");
        final User mrutte = new User("mrutte", Encryption.SHA256_BASE64("mrutte"));
        final User gbush = new User("gbush", Encryption.SHA256_BASE64("gbush"));
        for (Object o : Arrays.asList(policeman, civilServant, mrutte, gbush)) {
            em.persist(o);
        }
        mrutte.getRoles().add(civilServant);
        gbush.getRoles().addAll(Arrays.asList(civilServant, policeman));
        mrutte.setPicture("data:image/jpeg;base64,");
        gbush.setPicture("data:image/jpeg;base64,");

        final User[] users = new User[]{mrutte, gbush};
        final String[][] txtss = {{"@Samsom I rule!", "for now...."}, {"@Rutte: so do I"}};

        List<Tweet> tweets = new ArrayList<Tweet>();
        Random random = new Random();
        for (int i = 0; i < 5000; i++) {
            Tweet tweet = new Tweet();
            tweet.setAuthor(users[0]);
            Calendar calendar = new GregorianCalendar();
            calendar.set(2014, random.nextInt(11), random.nextInt(30));
            tweet.setCreationTimeMillis(calendar.getTimeInMillis());
            tweet.setText("Swek" + i);
            tweets.add(tweet);
        }
        
        Collections.sort(tweets, new TweetComparer());
        
        for(Tweet tweet: tweets){
            em.persist(tweet);
            users[0].addTweet(tweet);
        }
    }

    @PostConstruct
    void onPostConstruct() {
        pushAsync();
        fillDB();
    }

    public int getRandomHour(){
        Random random = new Random();
        
        return random.nextInt(23);
    }
    
    public int getRandomMinute(){
        Random random = new Random();
        
        return random.nextInt(59);
    }
    
    
    @Asynchronous
    @TransactionAttribute(TransactionAttributeType.NEVER)
    public void push() {
        ClientIncoming clientIncoming = new CurrentTime(System.currentTimeMillis());
        broadcaster.broadcast(TOPIC, Arrays.asList(clientIncoming));
        synchronized (this) {
            try {
                this.wait(5000);
                pushAsync();
            } catch (InterruptedException ex) {
                Logger.getLogger(PushyBean.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }

}
